package br.com.isaias.img.ui.photos

import androidx.lifecycle.ViewModelProvider
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.StaggeredGridLayoutManager
import br.com.isaias.img.R
import br.com.isaias.img.data.model.Photo
import br.com.isaias.img.databinding.ItemPhotosBinding
import br.com.isaias.img.databinding.PhotosFragmentBinding
import org.koin.androidx.viewmodel.ext.android.viewModel

class PhotosFragment : Fragment() {

    private val viewModel: PhotosViewModel by viewModel()
    private lateinit var binding: PhotosFragmentBinding
    private val adapter =  PhotosAdapter()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = PhotosFragmentBinding.inflate(inflater, container, false)
        setupAdapter()
        return binding.root
    }

    private fun setupAdapter() {
        binding.recyclerPhotos.adapter = adapter
        val layoutmanager = StaggeredGridLayoutManager(2, StaggeredGridLayoutManager.VERTICAL)
        layoutmanager.gapStrategy = StaggeredGridLayoutManager.GAP_HANDLING_MOVE_ITEMS_BETWEEN_SPANS
        binding.recyclerPhotos.layoutManager = layoutmanager
        binding.recyclerPhotos.setHasFixedSize(true)
        adapter.submitList(createMockPhotos())
    }

    fun createMockPhotos() = listOf<Photo>(
        Photo("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQNqnVYdqXDYc65FwT9F_ajnBL5_KKMUXQykg&usqp=CAU"),
        Photo("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQTgwTem5aX0QWoW4vfKQ9kJnSHji4wnuUwhA&usqp=CAU"),
        Photo("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSe9PXlwROJnzAmYA33WtcjPsrg3aSL0qNrug&usqp=CAU"),
        Photo("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSpn74W50Mc5g0ppvjFzw5grG3-BF_cfT2BNg&usqp=CAU"),
        Photo("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRRLp05BBkQYpYFEwdhN5CVUMXVPK6TpuKkFg&usqp=CAU"),
        Photo("data:image/jpeg;base64,"),
        Photo("data:image/jpeg;base64,"),
        Photo("data:image/jpeg;base64,"),
        Photo("data:image/jpeg;base64,")
    )
}